import React, { Fragment, useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { usersFetch, showForm, getIndex, clearIndex, editMode, addMode, deleteUser } from './actions';
import FormWrapper from './component/FormWrapper';
// import Header from './component/Header'

function App (props) {

  useEffect(() => {
    props.usersFetch()
  }, []);

  const editFrom = (index) => {
    props.showForm()
    props.clearIndex()
    props.addMode()
  }

  const addForm = () => {
    props.showForm()
    props.clearIndex()
    props.addMode()
  }

  const currentUsers = [...props.state.users]
  const confirmDeletion = (idxToDelete) => {
    if(window.confirm('delete user?')){
      props.deleteUser(currentUsers, idxToDelete)
      console.log(props.isDelete)
    } else {

    }
  }

  // One data left validation
  let isOneLeft = "show-delete-btn"
  if(props.state.users.length < 2) {
    isOneLeft= "hide-delte-btn"
  } else {
    isOneLeft = "show-delete-btn"
  }

  const usersItem = 
    props.state.users.map((user, index) => {
      return (
        <div className="main-container-tems" key={index}>
          <div className="itemsinfo">
            <img src={user.avatar} alt=""/>
            <p><strong>First Name:</strong>{user.first_name}</p>
            <p><strong>Last Name:</strong>{user.last_name}</p>
          </div>
          <div onClick={() => editFrom(index)} className="edit-btn">
            EDIT
          </div>
        </div>
      )
    });

    return (
      <Fragment>
        <FormWrapper />
        <div className="topbar">
          User Management
          <button className="add-btn" onClick={addForm}>Add User</button>
        </div>
        <div className="main-container">
          {usersItem}
        </div>
      </Fragment>
    )
};

const mapStateToProps = (store) => {
  return {
    state: store.usersReducer,
    userIndexToEdit: store.editReducer.userIndexToEdit,
    isDelete: store.editReducer.isDelete
  }
}

export default connect(mapStateToProps, {usersFetch, showForm, getIndex, clearIndex, editMode, addMode, deleteUser})(App);

